import React, { useState } from 'react';
import './App.css';
import { Plus, X } from 'lucide-react';
import ProductList from './ProductList';
import ProductForm from './ProductForm';

// Let's use a more detailed and structured mock data
const initialProducts = [
  {
    id: 1,
    productName: 'Imperial Leather Original Bar Soap',
    sku: 'ILS-175G',
    batchNumber: 'BATCH-IL-001',
    productType: 'Bar Soap',
    brand: 'Imperial Leather',
    variant: 'Original',
    status: 'Approved',
  },
  {
    id: 2,
    productName: 'Morning Fresh Original Dishwashing Liquid',
    sku: 'MFD-450ML-ORIG',
    batchNumber: 'BATCH-MF-002',
    productType: 'Dishwashing Liquid',
    brand: 'Morning Fresh',
    variant: 'Original',
    status: 'In Review',
  },
  {
    id: 3,
    productName: 'Morning Fresh Orange & Tea Tree',
    sku: 'MFD-450ML-OTT',
    batchNumber: 'BATCH-MF-003',
    productType: 'Dishwashing Liquid',
    brand: 'Morning Fresh',
    variant: 'Orange & Tea Tree',
    status: 'Draft',
  },
];

function App() {
  const [products, setProducts] = useState(initialProducts);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const addProduct = (newProduct) => {
    const productWithId = { ...newProduct, id: products.length + 1 };
    setProducts([...products, productWithId]);
    setIsFormVisible(false); // Hide form after submission
  };

  // Filter products based on search query (name or batch number)
  const filteredProducts = products.filter(
    (product) =>
      product.productName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.batchNumber.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="container">
      <header className="app-header">
        <h1>Product Management System</h1>
        <button className="add-product-btn" onClick={() => setIsFormVisible(true)}>
          <Plus size={20} />
          Add New Product
        </button>
      </header>

      {isFormVisible && (
        <div className="form-modal-overlay">
          <div className="form-modal">
            <div className="form-modal-header">
              <h2>Add a New Product</h2>
              <button className="close-btn" onClick={() => setIsFormVisible(false)}>
                <X size={24} />
              </button>
            </div>
            <ProductForm onAddProduct={addProduct} />
          </div>
        </div>
      )}

      <main>
        <div className="toolbar">
          <input
            type="text"
            placeholder="Search by name or batch number..."
            className="search-input"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
        <ProductList products={filteredProducts} />
      </main>
    </div>
  );
}

export default App;
